public class ZanoPlugin : BaseBTCPayServerPlugin
{
    public override IBTCPayServerPlugin.PluginDependency[] Dependencies { get; } =
    {
        new IBTCPayServerPlugin.PluginDependency { Identifier = nameof(BTCPayServer), Condition = ">=2.1.0" }
    };

    public override void Execute(IServiceCollection services)
    {
        var pluginServices = (PluginServiceCollection)services;
        var prov = pluginServices.BootstrapServices.GetRequiredService<NBXplorerNetworkProvider>();
        var chainName = prov.NetworkType;
        const string logo =
        "data:image/png;base64,";
        // Register ZANO network
        var zanoNetwork = new ZanoLikeSpecificBtcPayNetwork()
        {
            CryptoCode = "ZANO",
            DisplayName = "Zano",
            Divisibility = 12,
            DefaultRateRules = new[]
            {
                "ZANO_X = ZANO_BTC * BTC_X",
                "ZANO_BTC = zano(ZANO_BTC)",
                "ZANO_USD = zano(ZANO_USD)",
                "ZANO_EUR = zano(ZANO_EUR)"
            },
            CryptoImagePath = logo,
            UriScheme = "zano"
        };

        var zanoBlockExplorerLink = chainName == ChainName.Mainnet
            ? "https://explorer.zano.org/tx/{0}"
            : "https://testnet.explorer.zano.org/tx/{0}";

        var zanoPmi = PaymentTypes.CHAIN.GetPaymentMethodId("ZANO");
        services.AddDefaultPrettyName(zanoPmi, zanoNetwork.DisplayName);
        services.AddBTCPayNetwork(zanoNetwork)
                .AddTransactionLinkProvider(zanoPmi, new SimpleTransactionLinkProvider(zanoBlockExplorerLink));

        //// Register FUSD token network
