        //    UriScheme = "zano"
        //};

        //var fusdBlockExplorerLink = chainName == ChainName.Mainnet
        //    ? "https://explorer.zano.org/tx/{0}"
        //    : "https://testnet.explorer.zano.org/tx/{0}";

        //var fusdPmi = PaymentTypes.CHAIN.GetPaymentMethodId("FUSD");
        //services.AddDefaultPrettyName(fusdPmi, fusdNetwork.DisplayName);
        //services.AddBTCPayNetwork(fusdNetwork)
        //        .AddTransactionLinkProvider(fusdPmi, new SimpleTransactionLinkProvider(fusdBlockExplorerLink));


        services.AddSingleton(provider =>
                ConfigureZanoLikeConfiguration(provider));
        services.AddHttpClient("ZANOclient")
            .ConfigurePrimaryHttpMessageHandler(provider =>
            {
                var configuration = provider.GetRequiredService<ZanoLikeConfiguration>();
                if (!configuration.ZanoLikeConfigurationItems.TryGetValue("ZANO", out var zanoConfig) || zanoConfig.Username is null || zanoConfig.Password is null)
                {
                    return new HttpClientHandler();
                }
                return new HttpClientHandler
                {
                    Credentials = new NetworkCredential(zanoConfig.Username, zanoConfig.Password),
                    PreAuthenticate = true
                };
            });

        // FUSD reuses ZANO http client; no separate client required.
       
        services.AddSingleton<ZanoRPCProvider>();
        services.AddHostedService<ZanoLikeSummaryUpdaterHostedService>();
        services.AddHostedService<ZanoListener>();
        services.AddRateProvider<ZanoRateProvider>();


        // Register ZANO payment method handlers
        services.AddSingleton<IPaymentMethodHandler>(provider =>
                (IPaymentMethodHandler)ActivatorUtilities.CreateInstance(provider, typeof(ZanoLikePaymentHandler), new object[] { zanoNetwork }));
        services.AddSingleton<IPaymentLinkExtension>(provider =>
            (IPaymentLinkExtension)ActivatorUtilities.CreateInstance(provider, typeof(ZanoPaymentLinkExtension), new object[] { zanoNetwork, zanoPmi }));
        services.AddSingleton<ICheckoutModelExtension>(provider =>
            (ICheckoutModelExtension)ActivatorUtilities.CreateInstance(provider, typeof(ZanoCheckoutModelExtension), new object[] { zanoNetwork, zanoPmi }));
        services.AddSingleton<ICheckoutCheatModeExtension>(provider =>
            (ICheckoutCheatModeExtension)ActivatorUtilities.CreateInstance(provider, typeof(ZanoCheckoutCheatModeExtension), new object[] { zanoNetwork, zanoPmi }));

        // Register FUSD using same handlers, constructed with FUSD network
        //services.AddSingleton<IPaymentMethodHandler>(provider =>
        //    (IPaymentMethodHandler)ActivatorUtilities.CreateInstance(provider, typeof(ZanoLikePaymentHandler), new object[] { fusdNetwork }));
        //services.AddSingleton<IPaymentLinkExtension>(provider =>
        //    (IPaymentLinkExtension)ActivatorUtilities.CreateInstance(provider, typeof(ZanoPaymentLinkExtension), new object[] { fusdNetwork, fusdPmi }));
        //services.AddSingleton<ICheckoutModelExtension>(provider =>
